import React, { useState, useEffect } from 'react';
import './ElectronicSection.css';

const ElectronicsSection = ({ cart = [], onAddToCart, onRemoveFromCart }) => {
  const [electronicsData, setElectronicsData] = useState([]);

  useEffect(() => {
    fetch('http://127.0.0.1:5555/api/electronics')
      .then(response => response.json())
      .then(data => setElectronicsData(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const handleCartClick = (item) => {
    const itemInCart = cart.find(cartItem => cartItem.id === item.id);
    if (itemInCart) {
      onRemoveFromCart(itemInCart);
    } else {
      onAddToCart(item);
    }
  };

  const formatPrice = (price) => {
    return `ksh ${price.toLocaleString()}`;
  };

  return (
    <div className="electronics-section">
      <h2>Electronics</h2>
      <div className="electronics-grid">
        {electronicsData.map(item => (
          <ItemCard key={book.id} item={book} onAddToCart={onAddToCart} onRemoveFromCart={onRemoveFromCart} inCart={cart.includes(book)} />
        ))}
      </div>
    </div>
  );
};

export default ElectronicsSection;
